mapper.insert:
insert into article
	set regDate = now()
		, updateDate = now()
		, memberId = #{loginedMemberId}
		, boardId = #{boardId}
		, title = #{title} 
		, `body` = #{body}

mapper.update:
UPDATE article
    SET hitCnt = hitCnt + 1
    WHERE id = #{id }

mapper.select:
WITH am AS ( SELECT a.* ,m.nickname AS `writer` FROM article AS a INNER JOIN `member` AS m ON a.memberId = m.id WHERE a.boardId = #{boardId}
AND a.title like CONCAT('%', #{searchKeyword }, '%')
AND a.body like CONCAT('%', #{searchKeyword }, '%')
AND ( a.title like CONCAT('%', #{searchKeyword }, '%') OR a.body like CONCAT('%', #{searchKeyword }, '%') )
) SELECT am.* , IFNULL(sum(rp.point), 0) as sumReactionPoint , sum(IF(rp.point > 0, rp.point, 0)) AS goodReactionPoint , SUM(IF(rp.point < 0, rp.point, 0)) AS badReactionPoint FROM am left JOIN reactionPoint AS rp ON rp.relTypeCode = 'article' and am.id = rp.relId GROUP BY am.id ORDER BY am.id DESC LIMIT #{limitStart}, ${itemsInAPage}

mapper.update:
update article

		updateDate = now()

			, title = #{title}


			, `body` = #{body}


	where id = #{id}
